    isEmpty: function (obj) {
        for (var e in obj) {
            if (obj[e]) {
                return false;
            }
        }
        return true;
    },
    hasAllActives: function (obj1, obj2) {
        if (!obj1 || !obj2) {
            return false;
        }
        var contains = false;
        for (var p in obj1) {
            contains = false;
            for (var p2 in obj2) {
                if (p == p2) {
                    contains = true;
                    break;
                }
            }
            if (!contains) {
                return false;
            }
        }
        return true;
    },
    getActiveLayers: function () {
        var layers = "";
        for (var l in this.activeLayers) {
            if (layers.length) {
                layers += ',';
            }
            layers += l;
            if (!this.hasAllActives(SapoMapsApp.Layers[l].layers, this.activeLayers[l])) {
                var slayers = '';
                for (var sl in this.activeLayers[l]) {
                    if (slayers.length) {
                        slayers += '-';
                    }
                    slayers += sl;
                }
                layers += '-' + slayers;
            }
        }
        return layers;
    },
