        if (state.nc) {
            style.showLabelsInCentroid = state.nc === 0 ? false : true;
        }
        if (state.si) {
            style.showIcons = true;
            style.iconUrl = decodeURIComponent(state.si);
        }
        if (state.ac) {
            style.color = decodeURIComponent(state.ac);
        }
        if (state.so) {
            style.outlineColor = decodeURIComponent(state.so);
        }
